"use strict";

var DataStore = require('nedb')
    , config = require('db/config.js')
    , path = require('path');

console.log('path: ' + config.dbBasePath);
console.log('full: ' + path.join(config.dbBasePath, 'poc.db'));

var db = new DataStore({
    filename: (config.dbBasePath ? path.join(config.dbBasePath, 'poc.db') : undefined),
    autoload: true
});

module.exports = db;